import React from 'react'
import { useEffect } from 'react'
import { useState } from 'react'
import { Link } from 'react-router-dom'
import { useParams } from 'react-router-dom'
import ReactPlayer from 'react-player' 
import Videos from './Videos'
import { APIFetch } from '../utils/APIFetch'

//ReactPlayer - Accepts URL's

const VideoDetail = () => {
  const [videoDetail, setVideoDetail] = useState(null);
  const [videos, setVideos] = useState(null);
  const {id} = useParams();

  useEffect(() => {
    APIFetch(`videos?part=snippet,statistics&id=${id}`)
    .then((data) => setVideoDetail(data.items[0]))

    APIFetch(`search?part=snippet&relatedToVideoId=${id}&type=video`)
      .then((data) => setVideos(data.items))
  }, [id]);
  
  return (
    <div style={{minHeight: "100vh", position: 'relative', left: '72px', top: '80px'}}>
      <div style={{direction: {xs: 'column', md: 'row'}}}>
        <div style={{flex: 1}}>
          <div style={{sx: {width: '100%', position: 'sticky'}}}>
            <ReactPlayer url={`https://www.youtube.com/watch?v=${id}`}/>
          </div>
        </div>
      </div>
    </div>
  )
}

export default VideoDetail
