import { useState, useEffect } from "react"
import { useParams } from "react-router-dom"
import Videos from "./Videos"
import ChannelCard from "./ChannelCard"
import { APIFetch } from "../utils/APIFetch"

const ChannelDetail = () => {  
  const [channelDetail, setChannelDetail] = useState(null);
  const [videos, setVideos] = useState([]);

  const { id } = useParams();

  
  useEffect(() => {
    const fetchResults = async () => {
      const channelData = await APIFetch(`channels?part=snippet&id=${id}`);
      setChannelDetail(channelData?.items[0]);

      const videosData = await APIFetch(`search?channelId=${id}&part=snippet%2Cid&order=date`);
      setVideos(videosData?.items);
    };

    fetchResults();
  }, [id]);


  return (
    <div style={{minHeight: '95vh', left: '77px'}}>
      <div style={{height: '300px', boarder: '1px black', zIndex: '300'}}>

      </div>
    </div>
  )
}

export default ChannelDetail
